import React, { useEffect, useState } from "react";
import lista_palavras from './resources/words.json'
const max_typed=30
const getPalavra=()=>{
    const index=Math.floor(Math.random()*lista_palavras.length)
    const palavra =lista_palavras[index]
    return palavra.toLowerCase();
}
const App=()=>{
    const [typedKeys,setTypedKeys]=useState([])
    const [word,setWord]=useState('')
    const handleKeyDown=(e)=>{
        e.preventDefault();
        const {key}=e
        setTypedKeys((prev)=>{
            return [...prev,key].slice(-1*max_typed)
        })
    }
    useEffect(()=>{
        setWord(getPalavra())
    },[])
    return (
    
    <div className="container" tabIndex ="0" onKeyDown={handleKeyDown}>
       <div className="valid-keys">
           <span className="matched">emer</span>
           <span className="remainder">son</span>
       </div>
       <div className="typed-keys">{typedKeys?typedKeys.join(' '):null}</div> 
       <div className="completed-words-keys">
           <ol>
               <li>cidade</li>
               <li>carro</li>
               <li>professor</li>
               
           </ol>
       </div>
    </div>
    )
}
export default App;
